mapper.select:
select * from news_contribution where disabled = false and USER_ID=#{userId}

    and DOC_AUTHOR like concat('%',#{docAuthor},'%')


    and title like concat('%',#{title},'%')


    and status = #{status}


    and SUBMIT_TIME between #{startTime} and #{endTime}

limit #{start},#{size}

select * from news_contribution where disabled = false and USER_ID=#{userId}

    and DOC_AUTHOR like concat('%',#{docAuthor},'%')


    and title like concat('%',#{title},'%')


    and status = #{status}


    and SUBMIT_TIME between #{startTime} and #{endTime}

select count(*) from news_contribution where disabled = false and USER_ID=#{userId}

    and DOC_AUTHOR like #{docAuthor}


    and title like  concat('%',#{title},'%')


    and status = #{status}


    and SUBMIT_TIME between #{startTime} and #{endTime}
