mapper.insert:
insert into news_writer_contribution (ID, TITLE, CATEGROY_ID, 
  FILE_URL, STATUS, CREATE_USER_ID, 
  DISABLED, CREATE_TIME, UPDATE_TIME
  )
values (#{id,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, #{categroyId,jdbcType=VARCHAR}, 
  #{fileUrl,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{createUserId,jdbcType=VARCHAR}, 
  #{disabled,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select * from news_writer_contribution where disabled = false

    and title like #{name}

litmit #{start},#{size}
